# 
#   into
#
# >XXX_119601_B
# 
# >CZC_119601_F_b
# 

use strict;
use warnings;

my $verbose = 0;

my @output;
my $seqlen = -1;

sub update_seqlen($) {
    my $hap = shift;
    my $len = length($hap);
    if ($seqlen < 0) {
        $seqlen = $len;
    } elsif ($len != $seqlen) {
        die("inconsistent sequence length on input line $.: $len here vs. $seqlen elsewhere");
    }
}

while (<>) {
    next if not /^# ID +([^ ]+) /;
    my $id = $1;
    print STDERR "$id\n" if $verbose;
    my $h1 = <>;
    chomp $h1;
    $h1 =~ s/ //g;
    update_seqlen($h1);
    my $h2 = <>;
    chomp $h2;
    $h2 =~ s/ //g;
    update_seqlen($h2);
    push @output, ">$id";
    push @output, "$h1";
    push @output, ">${id}_b";
    push @output, "$h2";
}

print STDOUT scalar(@output)/2,"\t$seqlen\t1\n";
print STDOUT "$_\n" foreach @output;

